window.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".featured-row__item").forEach(function(a){const b=a.querySelector("video"),c=a.querySelector(".media--play-pause");c.addEventListener("click",d=>{d.preventDefault();b.paused?(b.play(),a.classList.add("video-playing"),c.classList.add("is-playing")):(b.pause(),a.classList.remove("video-playing"),c.classList.remove("is-playing"))})})});
