document.addEventListener("shopify:block:select",function(a){if(a.target.classList.contains("slideshow__slide")){var b=a.target.closest("slideshow-component");b.pause();setTimeout(function(){b.slider.scrollTo({left:a.target.offsetLeft})},200)}});document.addEventListener("shopify:block:deselect",function(a){a.target.classList.contains("slideshow__slide")&&(a=a.target.closest("slideshow-component"),a.autoplayButtonIsSetToPlay&&a.play())});
